            break;
	case 2 :
	    tell_room(TO,"%^RESET%^%^CRST%^%^C146%^The %^C241%^front door %^C146%^of the %^C254%^d%^C244%^o%^C252%^l%^C251%^l%^C250%^h%^C244%^o%^C252%^u%^C253%^s%^C254%^e %^C146%^swings open, seemingly of its own accord.%^CRST%^");
        default : 
